const skillsSection=document.querySelector(".our-skills"),spans=document.querySelectorAll(".prog span"),nums=document.querySelectorAll(".stats .box .number"),numsSection=document.querySelector(".stats");let countingStarted=!1;function isElementInViewport(t,n){return window.scrollY>=t.offsetTop-n}function animateSkillsSection(t){t.forEach(t=>{t.style.width=t.dataset.progress,t.classList.add("active")})}function startCountingAnimation(t){t.forEach(t=>startCount(t))}function startCount(t){const n=t.dataset.goal;let e=setInterval(()=>{t.textContent++,t.textContent===n&&clearInterval(e)},1500/n)}window.onscroll=function(){isElementInViewport(skillsSection,100)&&animateSkillsSection(spans),isElementInViewport(numsSection,400)&&!countingStarted&&(startCountingAnimation(nums),countingStarted=!0)};